import React, { Component } from 'react';
import Tags from "./Tags";
import Popular from "./Popular";
import NewsModal from "./NewsModal";

class News extends Component {
    constructor(props) {
        super(props);
        this.state = {
            blogs: [],
            error: null,
        };
    }

    componentDidMount() {
        this.fetchBlogs();
    }

    componentDidUpdate(prevProps) {
        if (prevProps.selectedCategory !== this.props.selectedCategory) {
            this.fetchBlogs();
        }
    }

    fetchBlogs() {
        const apiUrl = this.props.selectedCategory
            ? `http://localhost:3000/bloglar?kategoriId=${this.props.selectedCategory}`
            : 'http://localhost:3000/bloglar';

        fetch(apiUrl)
            .then((response) => response.json())
            .then((data) => {
                console.log('Gelen veri:', data);
                if (data) {
                    this.setState({ blogs: data });
                } else {
                    this.setState({ error: 'API verisi beklenen formatta değil' });
                }
            })
            .catch((error) => {
                console.error('Veri çekme hatası:', error);
                this.setState({ error: 'Veri çekme hatası' });
            });
    }

    handleBuyClick = (blogId) => {
        // Implement the logic for handling the buy action, e.g., redirect to a payment page
        console.log(`Buy button clicked for blog with ID: ${blogId}`);
    };

    render() {
        const { blogs, error } = this.state;

        if (error) {
            return <div>{error}</div>;
        }

        return (
            <div className="row mx-0">
                <div className="col-md-9 animate-box" data-animate-effect="fadeIn">
                    <div className="container-fluid pb-4 pt-4 paddding">
                        {blogs.map((blog, index) => (
                            <div key={index} className="row pb-4" onClick={() => this.props.onBlogClick(blog)}>
                                {/* ... (existing code) ... */}
                                <button
                                    className='rounded-1 mt-1 bg-success text-white'
                                    onClick={() => this.handleBuyClick(blog.id)} // Pass the blog ID to the handler
                                >
                                    Satın AL
                                </button>
                                {/* ... (existing code) ... */}
                            </div>
                        ))}
                    </div>
                </div>
                {/* ... (existing code) ... */}
            </div>
        );
    }
}

export default News;
